mapper.select:
SELECT MEM_ID AS memId
FROM MEMBER
WHERE MEM_NAME = #{memName} AND MEM_TEL = #{memTel}

   		SELECT CASE WHEN COUNT(*) >= 1 THEN 'Y' ELSE 'N' END AS ISEMP
		FROM MEMBER
		WHERE MEM_NAME = #{memName} AND MEM_TEL = #{memTel}

   		SELECT CASE WHEN COUNT(*) >= 1 THEN 'Y' ELSE 'N' END AS ISEMP
		FROM MEMBER
		WHERE MEM_ID = #{memId} AND MEM_PW = #{memPw}

   		SELECT MEM_ID AS memId, MEM_NAME AS memName
		FROM MEMBER
		WHERE MEM_ID = #{memId} AND MEM_PW = #{memPw}

SELECT MEM_ID AS memId, MEM_PW AS memPw, MEM_NAME AS memName, MEM_INTRO AS memIntro, MEM_TEL AS memTel, GEN_CODE AS memGender, CATE_CODE1 AS memCate1, CATE_CODE2 AS memCate2
FROM MEMBER
WHERE MEM_ID = #{memId}
